/**
 * Normalizes a word.
 * @private
 * @example
 *
 *     normalize('cloud layer')
 *     => 'Cloudlayer'
 *
 *     normalize('time ible')
 *     => 'Timible'
 */

function normalize(word) {
  return word.replace(/e i/, 'i') // time ible -> timible
  .replace(/th t/, 't') // health tion -> healthion
  .replace(/(.) (.)/, function (_, a, b) {
    return a === b ? a : "".concat(a).concat(b);
  }) // live event -> livevent
  .replace(/^./, function (s) {
    return s.toUpperCase();
  });
}

/*
 * Exports
 */

module.exports = {
  normalize: normalize
};
