export default function SelectSearchInside({
  value,
  onChange,
  toggleClass,
  menuClass,
  placeholder,
  dropdown_container,
  ...props
}) {
  const wrapperRef = useRef(null)
  const [show, setShow] = useState(false)
  const [temp, setTemp] = useState(value)
  const openMenu = () => {
    setShow(!show)
  }

  useOnClickOutside(wrapperRef, () => {
    setShow(false)
  })

  const getLabel = () => {
    if (value) {
      if (value.value && value.label && value.value !== '') {
        return <span style={{ whiteSpace: 'break-spaces' }}>{value.label}</span>
      } else {
      }
    }
    if (temp) {
      if (temp.value && temp.label && temp.value !== '') {
        return <span style={{ whiteSpace: 'break-spaces' }}>{temp.label}</span>
      }
    }
    return (
      <span
        className='text-muted'
        style={{ minHeight: 24, whiteSpace: 'break-spaces' }}
      >
        {placeholder ? placeholder : ''}
      </span>
    )
  }

  return (
    <div style={{ width: `100%` }}>
      {/* <style jsx global>
        {`
          .select-with-search-inside::after {
            display: none;
          }
        `}
      </style> */}
      <Dropdown
        className={dropdown_container ? dropdown_container : ''}
        ref={wrapperRef}
        style={{
          width: `100%`,
          height: `100%`
        }}
        show={show}
      >
        <Dropdown.Toggle
          style={{
            borderWidth: 1,
            borderColor: show ? '#9c9c9c' : '#ccc'
          }}
          className={`select-with-search-inside text-dark ${
            styles.dropdown_toggle
          } ${toggleClass ? ' ' + toggleClass : ''}`}
          onClick={openMenu}
        >
          {getLabel()}
          <div style={{ minWidth: 13 }}>
            <FaChevronDown size={13} color={show ? '#6e6e6e' : '#ccc'} />
          </div>
        </Dropdown.Toggle>
        <Dropdown.Menu
          className={`${styles.dropdown_menu} ${
            menuClass ? ' ' + menuClass : ''
          }`}
          show={show}
        >
          <div id='select-panel'>
            {show ? (
              <Select
                {...props}
                styles={customStyles}
                autoFocus
                menuIsOpen
                components={{
                  IndicatorSeparator: () => null,
                  DropdownIndicator: () => null,
                  Option: (props) => (
                    <Option result={value ? value : temp} {...props} />
                  )
                }}
                onChange={(value) => {
                  if (onChange) {
                    onChange(value)
                  }
                  setTemp(value)
                  setShow(false)
                }}
              />
            ) : (
              <div></div>
            )}
          </div>
        </Dropdown.Menu>
      </Dropdown>
    </div>
  )
}
