import { useState } from 'react'
import style from '../Index.module.scss'
import React from 'react'
import { FaAngleDoubleUp } from 'react-icons/fa'
import _ from 'lodash'

var restricted_countries = [
  'Cambodia',
  'Jordan',
  'Myanmar',
  'Pakistan',
  'Philippines',
  'Syria',
  'Turkey',
  'Yemen',
  'Iran',
  'The Democratic People’s Republic of Korea',
  'Iraq',
  'Afghanistan',
  'Turkmenistan',
  'Albania',
  'Gibraltar',
  'Barbados',
  'Haiti',
  'Jamaica',
  'US Virgin Islands',
  'Trinidad and Tobago',
  'Venezuela',
  'Burkina Faso',
  'Mali',
  'Morocco',
  'Senegal',
  'South Sudan',
  'Uganda',
  'Somalia',
  'Democratic Republic of the Congo',
  'Sudan',
  'Libya',
  'Central African Republic',
  'Nigeria',
  'Burundi',
  'Equatorial Guinea',
  'Guinea-Bissau',
  'Samoa',
  'Fiji',
  'Palau',
  'American Samoa',
  'Vanuatu',
  'Guam',
  'Niger',
  'Crimea Region',
  'Donetsk Region',
  'Luhansk Region',
  'Cuba',
  'Tanzania',
  'Mozambique'
]

const FatfScreen = ({ onReceiveData }) => {
  const [isCollapse, setIsCollapse] = useState(true)

  const handleCollapseToggle = () => {
    if (isCollapse) {
      setIsCollapse(false)
    } else {
      setIsCollapse(true)
    }
  }

  return (
    <section className={style.FatfScreen}>
      <div className='d-none d-lg-block' style={{ height: '95px' }}></div>
      <h2 className='mt-0'>Restricted Industries</h2>
      <div>
        <p className='mb-lg-3 mb-2'>
          Pursuant to the anti-money laundering and countering terrorism
          regulations, BBCIncorp will not provide our services for customers,
          who are involved in restricted industries including but not limited
          to:
        </p>
        <p className='mb-lg-0 mb-2'>
          Weapons dealing; Pornography/adult entertainment; Marijuana-related
          business; Money service business; Gambling-related business; Money
          remittance and payment services; Tobacco business; Alcohol business;
          Debt loan collection business; Precious metal trading business;
          sensitive virtual asset businesses.
        </p>
      </div>
      <h2 className='mt-0 mt-lg-3'>Restricted Territories List</h2>
      <div>
        <p className='mb-lg-3 mb-2'>
          Before proceeding to the incorporation process, kindly note that
          BBCIncorp DOES NOT provide services for nationalities under the below
          list.
        </p>
        <p className='mb-lg-0 mb-2'>
          The list is made based on recommendations by credible sources such as
          FATF as part of our effort to comply with AML/CTF regulations and is
          reviewed and updated from time to time.
        </p>
      </div>
      <div className={`px-3 p-lg-4`}>
        <div className='position-relative pb-5'>
          <div
            className={`${style.listCollapse} ${!isCollapse && style.active}`}
          >
            {/* <ul className="row ml-2 ml-lg-40 mb-0">
              {_.sortBy(restricted_countries).map((item, index) => (
                <li key={index} className="col-6 col-md-4 mb-1">{item}</li>
              ))}
            </ul> */}
            <div className='row d-none d-md-flex'>
              {new Array(3).fill(3).map((item, idx) => {
                let count = restricted_countries.length / 3
                return (
                  <div key={idx} className='col-4'>
                    {_.sortBy(restricted_countries)
                      .filter(
                        (item, index) =>
                          index < (idx + 1) * count && index >= idx * count
                      )
                      .map((item, index) => {
                        return (
                          <div key={index} className={`${style.item} mb-1`}>
                            {item}
                          </div>
                        )
                      })}
                  </div>
                )
              })}
            </div>
            <div className='row d-md-none'>
              {new Array(2).fill(2).map((item, idx) => {
                let count = restricted_countries.length / 2
                return (
                  <div key={idx} className='col-6'>
                    {_.sortBy(restricted_countries)
                      .filter(
                        (item, index) =>
                          index < (idx + 1) * count && index >= idx * count
                      )
                      .map((item, index) => {
                        return (
                          <div key={index} className={`${style.item} mb-1`}>
                            {item}
                          </div>
                        )
                      })}
                  </div>
                )
              })}
            </div>
          </div>
          <div
            className={`${style.buttonCollapse} ${
              isCollapse && 'pt-4 pt-lg-40'
            } pb-3 pb-lg-0`}
          >
            <button onClick={handleCollapseToggle}>
              {isCollapse ? 'Expand' : 'Collapse'}
              <FaAngleDoubleUp className={isCollapse && style.iconCollapse} />
            </button>
          </div>
        </div>
      </div>
      <p className={`mb-0 mt-2 mt-lg-0 ${style.noteText}`}>
        <b>Note:</b> Nationalities under consideration are applicable to all
        members of your company, including directors, shareholders and
        beneficial owners.
      </p>
      <div className='text-center mt-3 mt-lg-4'>
        <button className={style.Button} onClick={onReceiveData}>
          I understand
        </button>
      </div>
    </section>
  )
}

export default FatfScreen
import Select, { components } from "react-select";
import Dropdown from "react-bootstrap/Dropdown";
import { useState, useRef } from "react";
import { useOnClickOutside } from "common/window";
import { FaChevronDown } from "react-icons/fa";

const customStyles = {
  menu: (provided) => ({
    ...provided,
    position: "relative",
    boxShadow: "none",
    marginBottom: 0,
  }),
  control: (provided) => ({
    ...provided,
    margin: "0px 8px",
  }),
}

const Option = ({ data, result, children, ...props }) => {
  return <components.Option className={`p-0 ${data && result && data.value === result.value ? " bg-primary text-white" : ""}`} {...props}>
    <div style={{ cursor: "pointer", padding: "8px 12px" }}>{children}</div>
  </components.Option>;
}

export default function SelectSearchInside({ value, onChange, toggleClass, menuClass, placeholder, ...props }) {
  const wrapperRef = useRef(null);
  const [show, setShow] = useState(false);
  const [temp, setTemp] = useState(value);
  const openMenu = () => {
    setShow(!show);
  }

  useOnClickOutside(wrapperRef, () => {
    setShow(false);
  });

  const getLabel = () => {
    if (value) {
      if (value.value && value.label && value.value !== "") {
        return <span style={{ whiteSpace: "break-spaces" }}>{value.label}</span>;
      } else {
      }
    }
    if (temp) {
      if (temp.value && temp.label && temp.value !== "") {
        return <span style={{ whiteSpace: "break-spaces" }}>{temp.label}</span>;
      }
    }
    return <span className="text-muted" style={{ minHeight: 24 , whiteSpace: "break-spaces" }}>{placeholder ? placeholder : ""}</span>;
  }

  return <>
    <style jsx global>{`
        .select-with-search-inside::after {
          display: none;
        }
      `}
    </style>
    <Dropdown ref={wrapperRef} className="w-100" show={show}>
      <Dropdown.Toggle
        style={{
          borderWidth: 1,
          borderColor: show ? "#9c9c9c" : "#ccc",
        }}
        className={`overflow-hidden w-100 py-2 bg-white shadow-none text-dark text-left select-with-search-inside d-flex justify-content-between align-items-center${toggleClass ? " " + toggleClass : ""}`}
        onClick={openMenu}
      >
        {getLabel()}
        <div style={{ minWidth: 13 }}>
          <FaChevronDown size={13} color={show ? "#6e6e6e" : "#ccc"} />
        </div>
      </Dropdown.Toggle>
      <Dropdown.Menu className={`w-100 pb-0 ${menuClass ? " " + menuClass : ""}`} show={show}>
        <div id="select-panel">
          {show ? <Select
            {...props}
            styles={customStyles}
            autoFocus
            menuIsOpen
            components={{ IndicatorSeparator: () => null, DropdownIndicator: () => null, Option: (props) => <Option result={value ? value : temp} {...props} /> }}
            onChange={(value) => {
              if (onChange) {
                onChange(value);
              }
              setTemp(value);
              setShow(false);
            }}
          /> : <></>}
        </div>
      </Dropdown.Menu>
    </Dropdown></>
}
